import { Component, OnInit } from '@angular/core';
import { CryptocoinService, cryptocoin } from '../cryptocoin.service';

@Component({
  selector: 'app-cryptocoin-list',
  templateUrl: './cryptocoin-list.component.html',
  styleUrls: ['./cryptocoin-list.component.css']
})
export class CryptocoinListComponent implements OnInit{
  cryptocoinList : cryptocoin[] = [];

  constructor(public cryptocoinService: CryptocoinService){};

  ngOnInit(): void {
    this.cryptocoinService.getCurrencies("BTC,ETH,BNB,USDT,ADA").subscribe(data => this.cryptocoinList = data);
  }
}
